export const getUserLanguage = () => {
    const userLang = navigator.language || navigator.userLanguage
    let language = ''
    switch (userLang) {
        case 'pt-BR':
            language = 'pt_BR'
            break
        case 'en-US':
            language = 'en_US'
            break
        case 'es':
            language = 'es_ES'
            break
        case 'es-ES':
            language = 'es_ES'
            break
        default:
            language = 'pt_BR'
    }
    return language
}


export const getSurveyLanguage = (userLanguage, widget) => {
    if (widget.survey.lang && widget.survey.lang.length > 0) {
        if (widget.survey.lang.includes(userLanguage)) {
            return userLanguage
        }
    }
    return widget.survey.default_lang
}

export const userLanguage = () => {
    if (store.state.survey.language === 'pt_BR') {
        return ptBR
    }
    if (store.state.survey.language === 'es_ES') {
        return esES
    }
    return enUS
}
